import React,{Component} from 'react';
import Properties from './components/properties';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  state = {
    properties: []
  }

  componentDidMount() {
    fetch('http://localhost:4000/properties')
    .then(res => res.json())
    .then((data) => {
      this.setState({ properties: data })
    })
    .catch(console.log)
  }

  filterProperties(input) {
    console.log(input);
    fetch(`http://localhost:4000/filterItem/?filterItem=${input}`)
    .then(res => res.json())
    .then((data) => {
      console.log(data);
      this.setState({ properties: data })
    })
    .catch(console.log)
  }

  handleSubmit(e) {
    this.filterProperties(this.cityInput.value);
    e.preventDefault();
  }
  render() {
      return (
         <div>
           <div  className={"centered"}>
              <form onSubmit={this.handleSubmit}>
                <label>
                  Name:
                  <input type="text" ref={(nameInput) => this.nameInput = nameInput} />
                </label>
                <label>
                  City:
                  <input type="text" ref={(cityInput) => this.cityInput = cityInput} />
                </label>
                <label>
                  State:
                  <input type="text" ref={(stateInput) => this.stateInput = stateInput} />
                </label>
                <input type="submit" value="Submit" />
              </form>
           </div>
           <div  className={"centered"}>
              <Properties properties={this.state.properties} />
           </div>
         </div>
      )
  }
}

export default App;
